import React from 'react'
import Navigation from '../components/Navigation'
import Timeline from '../components/Timeline'
import Messagebar from '../components/Messagebar'
import Createpost from '../components/Createpost'
import Requests from '../components/Requests'
import { useRef,useEffect } from 'react'
const Home = () => {
    const timelineRef = useRef();
    const messagebarRef = useRef();
  
    const handleScroll = () => {
      // Get the scroll position of each section
      const timelineScroll = timelineRef.current.scrollTop;
      const messagebarScroll = messagebarRef.current.scrollTop;
  
      // Use the scroll positions as needed
      // You can add custom logic based on the scroll positions of each section
      console.log('Timeline Scroll:', timelineScroll);
      console.log('Messagebar Scroll:', messagebarScroll);
    };
  
    useEffect(() => {
      // Attach the scroll event listener when the component mounts
      const timelineNode = timelineRef.current;
      const messagebarNode = messagebarRef.current;
  
      timelineNode.addEventListener('scroll', handleScroll);
      messagebarNode.addEventListener('scroll', handleScroll);
  
      // Remove the event listener when the component unmounts
      return () => {
        timelineNode.removeEventListener('scroll', handleScroll);
        messagebarNode.removeEventListener('scroll', handleScroll);
      };
    }, []);
  
    return (
      <>
        <div className='bg-[#F1F1F1] h-fit w-full flex py-6'>
          <div className='w-[22%]'>
            <div className='fixed w-[22%] min-h-screen  flex flex-col gap-8 pl-20'>
              <Navigation />
              <Createpost />
            </div>
          </div>
          <div className='w-[50%]' ref={timelineRef}>
            <Timeline />
          </div>
          <div className='w-[28%]' ref={messagebarRef}>
            <div className='w-full min-h-screen flex flex-col overflow-y-scroll gap-8 pr-20 pl-6'>
              <Messagebar />
              <Requests />
            </div>
          </div>
        </div>
      </>
  )
}

export default Home
